mapper.select:
select sdc.create_dt, sdc.gubun, sdc.inc_dec, sdc.def_cnt, vsdcd.inc_dec as inc_dec_distance, vsdcd.def_cnt as def_cnt_distance, ci.country_id from si_do_case sdc join v_si_do_case_distance vsdcd on sdc.CREATE_DT = vsdcd.create_dt and sdc.gubun = vsdcd.gubun left join country_id ci on sdc.gubun = ci.country_name
and sdc.create_dt = #{create_dt}
and sdc.create_dt = (select max(create_dt) from si_do_case)
order by sdc.CREATE_DT desc, sort, sdc.inc_dec desc;

select * from (select DATE_FORMAT(CREATE_DT,'%y-%m-%d') as create_dt, sum(inc_dec) as inc_dec from si_do_case sdc left join country_id ci on sdc.gubun = ci.country_name where GUBUN != '합계'
and create_dt <= #{create_dt}
and create_dt <= (select max(create_dt) from si_do_case)
and ci.country_id = #{country_id}
group by CREATE_DT order by CREATE_DT desc limit 0,7) a order by a.create_dt asc

mapper.insert:
INSERT INTO covidplus.kakao_bot_user_option
(user_name, option_name, option_value, description)
VALUES(#{user_name}, #{option_name}, #{option_value}, #{description});
